import React, { Component} from 'react';
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import './Family.css';

const Tabs = React.createClass({
  getInitialState(){
    return {
      selected:this.props.selected || 0
    }
  },
  render(){
    return (<div>
      <ul className="inline">
        {this.props.children.map((elem,index)=>{
          let style = index == this.state.selected ? 'selected': '';
         return <li className={style} key={index} onClick={this.handleChange.bind(this,index)}>{elem.props.title}</li>
        })}
      </ul>
      <div className="tab">{this.props.children[this.state.selected]}</div>
      </div>
    )
  },
    handleChange(index){
      this.setState({selected:index})
    }
})

const Panel = React.createClass({
  render(){
    return <div>{this.props.children}</div>
  }
})



const responsive = {
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 4,
    slidesToSlide: 4 // optional, default to 1.
  },
  tablet: {
    breakpoint: { max: 1024, min: 464 },
    items: 2,
    slidesToSlide: 2 // optional, default to 1.
  },
  mobile: {
    breakpoint: { max: 464, min: 0 },
    items: 1,
    slidesToSlide: 1 // optional, default to 1.
  }
};

class Family extends Component{

    render(){
        return(
            <div className="family" id="family">
                <div className="container">


                <Tabs selected={1}>
                  <Panel title="first">This is the first panel</Panel>
                  <Panel title="second">This is the second panel</Panel>
                  <Panel title="third">This is the third panel</Panel>
              </Tabs>




                    <h2 className="head">Loveble Family</h2>
                    <div className="family-tab">
                        <img src="/images/bibin.jpg" alt="faceimg"/>
                        <img src="/images/bibin.jpg" alt="faceimg"/>
                    </div>
                    <div className="familycarousel">
                        <Carousel
                        swipeable={false}
                        draggable={false}
                        showDots={this.props.deviceType === "mobile" ? true : false}
                        responsive={responsive}
                        ssr={true} // means to render carousel on server-side.
                        infinite={true}
                        autoPlay={this.props.deviceType === "mobile" ? true : false}
                        autoPlaySpeed={2000}
                        keyBoardControl={true}
                        customTransition="all .5"
                        transitionDuration={500}
                        containerClass="carousel-container"
                        removeArrowOnDeviceType={["desktop"]}
                        deviceType={this.props.deviceType}
                        dotListClass="custom-dot-list-style"
                        itemClass="carousel-item-padding-40-px"
                        >
                          <div>
                            <img src="/images/sunny.jpg" alt="faceimg"/>
                            <img src="/images/bency.jpg" alt="faceimg"/>
                            <img src="/images/disna.png" alt="faceimg"/>
                            <img src="/images/roshni.jpg" alt="faceimg"/>
                          </div>

                          <div>
                            <img src="/images/roshni.jpg" alt="faceimg"/>
                          </div>

                        </Carousel>;
                    </div>
                </div>

             </div>
        )
    }
}

export default Family;
