import React, {Fragment} from 'react';
import Column from './Column';

//filter hook
const getColumns = (imgarr, col) =>{
    let images=[]
    for (let i = 0; i < col; i++) {
        images.push([])

    }
    let sum =0;
    imgarr.forEach(img => {
        sum += Number(img.height);

    });
    let thershold =sum/4;
    let current = 0;
    let l = 0;

    imgarr.forEach(img =>{

        if((current + Number(img.height)) >= thershold){
            if(l != col){l += 1}
            l += 1;
            current = Number(img.height);
            images[l].push(img)
        }else{
            current += Number(img.height);
            images[l].push(img)
        }
    })
    return images
}
function Gallery({imgarr}){
    let columns = getColumns(imgarr);

    return(
        <Fragment>
            { 
                columns.map((img, i) => {
                    return <Column  key={i} images={img}/>
                })

            }
        </Fragment>
    )
}
export default Gallery
