import React from 'react';
import Column from './Column';

function Gallery({imgarr}){

    let images=[[],[],[],[]]

    let sum =0;
    imgarr.forEach(img => {
        sum += Number(img+height);

    });
    let thershold =sum/4;
    let current = 0;
    let l = 0;
    imgarr.forEach(img =>{

        if(current >= thershold){
            l+=1;
            current=0;
            images[l].push(img)
        }else{
            current += Number(img.height)
            images[l].push(img)
        }
    })
    return(
        <div>
            {

                imgarr.map((img, i) => {
                    return <Column  key={i}images={images}/>
                })

            }

        </div>
    )
}
export default Gallery
